package fr.acinq.eclair.channel

import fr.acinq.bitcoin.Transaction
import fr.acinq.eclair.channel.Helpers.Closing.{CurrentRemoteClose, LocalClose, MutualClose, NextRemoteClose, RecoveryClose, RevokedClose}
import fr.acinq.eclair.channel.Helpers.{Closing, Funding}
import org.scalatest.FunSuite

import scala.compat.Platform
import scala.concurrent.duration._

class HelpersSpec extends FunSuite {

  test("compute funding timeout") {
    // we are supposed to wait 2 hours, and have already been waiting 1 hour, we have one more hour to wait
    assert(Funding.computeFundingTimeout(
      now = 7200,
      waitingSince = 3600,
      delay = 2 hours,
      minDelay = 10 minutes) === (1 hour))

    // we are supposed to wait 2 hours, and have already been waiting 9 hours, we'll wait the minimum of 10 minutes
    assert(Funding.computeFundingTimeout(
      now = 36000,
      waitingSince = 3600,
      delay = 2 hours,
      minDelay = 10 minutes) === (10 minutes))

    // we are supposed to wait 5 minutes, and have already been waiting 4 minutes, we'll wait the minimum of 10 minutes
    assert(Funding.computeFundingTimeout(
      now = 1240,
      waitingSince = 1000,
      delay = 5 minutes,
      minDelay = 10 minutes) === (10 minutes))
  }

  test("compute refresh delay") {
    import org.scalatest.Matchers._
    implicit val log = akka.event.NoLogging
    Helpers.nextChannelUpdateRefresh(1544400000).toSeconds should equal(0)
    Helpers.nextChannelUpdateRefresh((Platform.currentTime.milliseconds - 9.days).toSeconds).toSeconds should equal(24 * 3600L +- 100)
    Helpers.nextChannelUpdateRefresh((Platform.currentTime.milliseconds - 3.days).toSeconds).toSeconds should equal(7 * 24 * 3600L +- 100)
    Helpers.nextChannelUpdateRefresh(Platform.currentTime.milliseconds.toSeconds).toSeconds should equal(10 * 24 * 3600L +- 100)
  }

  test("tell closing type") {
    val tx1 = Transaction.read("")
    val tx2 = Transaction.read("")
    val tx3 = Transaction.read("")
    val tx4 = Transaction.read("")
    val tx5 = Transaction.read("")
    val tx6 = Transaction.read("")

    // only mutual close
    assert(Closing.isClosingTypeAlreadyKnown(
      DATA_CLOSING(
        commitments = null,
        mutualCloseProposed = tx1 :: tx2 :: tx3 :: Nil,
        mutualClosePublished = tx2 :: tx3 :: Nil,
        localCommitPublished = None,
        remoteCommitPublished = None,
        nextRemoteCommitPublished = None,
        futureRemoteCommitPublished = None,
        revokedCommitPublished = Nil)
    ).isEmpty)

    // mutual + local close, but local commit tx isn't confirmed
    assert(Closing.isClosingTypeAlreadyKnown(
      DATA_CLOSING(
        commitments = null,
        mutualCloseProposed = tx1 :: Nil,
        mutualClosePublished = tx1 :: Nil,
        localCommitPublished = Some(LocalCommitPublished(
          commitTx = tx2,
          claimMainDelayedOutputTx = Some(tx3),
          htlcSuccessTxs = Nil,
          htlcTimeoutTxs = Nil,
          claimHtlcDelayedTxs = Nil,
          irrevocablySpent = Map.empty
        )),
        remoteCommitPublished = None,
        nextRemoteCommitPublished = None,
        futureRemoteCommitPublished = None,
        revokedCommitPublished = Nil)
    ).isEmpty)

    // mutual + local close, local commit tx confirmed
    assert(Closing.isClosingTypeAlreadyKnown(
      DATA_CLOSING(
        commitments = null,
        mutualCloseProposed = tx1 :: Nil,
        mutualClosePublished = tx1 :: Nil,
        localCommitPublished = Some(LocalCommitPublished(
          commitTx = tx2,
          claimMainDelayedOutputTx = Some(tx3),
          htlcSuccessTxs = Nil,
          htlcTimeoutTxs = Nil,
          claimHtlcDelayedTxs = Nil,
          irrevocablySpent = Map(
            tx2.txIn.head.outPoint -> tx2.txid
          )
        )),
        remoteCommitPublished = None,
        nextRemoteCommitPublished = None,
        futureRemoteCommitPublished = None,
        revokedCommitPublished = Nil)
    ).contains(LocalClose))

    // local close + remote close, none is confirmed
    assert(Closing.isClosingTypeAlreadyKnown(
      DATA_CLOSING(
        commitments = null,
        mutualCloseProposed = Nil,
        mutualClosePublished = Nil,
        localCommitPublished = Some(LocalCommitPublished(
          commitTx = tx2,
          claimMainDelayedOutputTx = None,
          htlcSuccessTxs = Nil,
          htlcTimeoutTxs = Nil,
          claimHtlcDelayedTxs = Nil,
          irrevocablySpent = Map.empty
        )),
        remoteCommitPublished = Some(RemoteCommitPublished(
          commitTx = tx3,
          claimMainOutputTx = None,
          claimHtlcSuccessTxs = Nil,
          claimHtlcTimeoutTxs = Nil,
          irrevocablySpent = Map.empty
        )),
        nextRemoteCommitPublished = None,
        futureRemoteCommitPublished = None,
        revokedCommitPublished = Nil)
    ).isEmpty)

    // mutual + local + remote close, remote commit tx confirmed
    assert(Closing.isClosingTypeAlreadyKnown(
      DATA_CLOSING(
        commitments = null,
        mutualCloseProposed = tx1 :: Nil,
        mutualClosePublished = tx1 :: Nil,
        localCommitPublished = Some(LocalCommitPublished(
          commitTx = tx2,
          claimMainDelayedOutputTx = None,
          htlcSuccessTxs = Nil,
          htlcTimeoutTxs = Nil,
          claimHtlcDelayedTxs = Nil,
          irrevocablySpent = Map.empty
        )),
        remoteCommitPublished = Some(RemoteCommitPublished(
          commitTx = tx3,
          claimMainOutputTx = None,
          claimHtlcSuccessTxs = Nil,
          claimHtlcTimeoutTxs = Nil,
          irrevocablySpent = Map(
            tx3.txIn.head.outPoint -> tx3.txid
          )
        )),
        nextRemoteCommitPublished = None,
        futureRemoteCommitPublished = None,
        revokedCommitPublished = Nil)
    ).contains(CurrentRemoteClose))

    // mutual + local + remote + next remote close, next remote commit tx confirmed
    assert(Closing.isClosingTypeAlreadyKnown(
      DATA_CLOSING(
        commitments = null,
        mutualCloseProposed = tx1 :: Nil,
        mutualClosePublished = tx1 :: Nil,
        localCommitPublished = Some(LocalCommitPublished(
          commitTx = tx2,
          claimMainDelayedOutputTx = None,
          htlcSuccessTxs = Nil,
          htlcTimeoutTxs = Nil,
          claimHtlcDelayedTxs = Nil,
          irrevocablySpent = Map.empty
        )),
        remoteCommitPublished = Some(RemoteCommitPublished(
          commitTx = tx3,
          claimMainOutputTx = None,
          claimHtlcSuccessTxs = Nil,
          claimHtlcTimeoutTxs = Nil,
          irrevocablySpent = Map.empty
        )),
        nextRemoteCommitPublished = Some(RemoteCommitPublished(
          commitTx = tx4,
          claimMainOutputTx = Some(tx5),
          claimHtlcSuccessTxs = Nil,
          claimHtlcTimeoutTxs = Nil,
          irrevocablySpent = Map(
            tx4.txIn.head.outPoint -> tx4.txid
          )
        )),
        futureRemoteCommitPublished = None,
        revokedCommitPublished = Nil)
    ).contains(NextRemoteClose))

    // future remote close, not confirmed
    assert(Closing.isClosingTypeAlreadyKnown(
      DATA_CLOSING(
        commitments = null,
        mutualCloseProposed = Nil,
        mutualClosePublished = Nil,
        localCommitPublished = None,
        remoteCommitPublished = None,
        nextRemoteCommitPublished = None,
        futureRemoteCommitPublished = Some(RemoteCommitPublished(
          commitTx = tx4,
          claimMainOutputTx = Some(tx5),
          claimHtlcSuccessTxs = Nil,
          claimHtlcTimeoutTxs = Nil,
          irrevocablySpent = Map.empty
        )),
        revokedCommitPublished = Nil)
    ).isEmpty)

    // future remote close, confirmed
    assert(Closing.isClosingTypeAlreadyKnown(
      DATA_CLOSING(
        commitments = null,
        mutualCloseProposed = Nil,
        mutualClosePublished = Nil,
        localCommitPublished = None,
        remoteCommitPublished = None,
        nextRemoteCommitPublished = None,
        futureRemoteCommitPublished = Some(RemoteCommitPublished(
          commitTx = tx4,
          claimMainOutputTx = Some(tx5),
          claimHtlcSuccessTxs = Nil,
          claimHtlcTimeoutTxs = Nil,
          irrevocablySpent = Map(
            tx4.txIn.head.outPoint -> tx4.txid
          )
        )),
        revokedCommitPublished = Nil)
    ).contains(RecoveryClose))

    // local close + revoked close, none confirmed
    assert(Closing.isClosingTypeAlreadyKnown(
      DATA_CLOSING(
        commitments = null,
        mutualCloseProposed = Nil,
        mutualClosePublished = Nil,
        localCommitPublished = Some(LocalCommitPublished(
          commitTx = tx1,
          claimMainDelayedOutputTx = None,
          htlcSuccessTxs = Nil,
          htlcTimeoutTxs = Nil,
          claimHtlcDelayedTxs = Nil,
          irrevocablySpent = Map.empty
        )),
        remoteCommitPublished = None,
        nextRemoteCommitPublished = None,
        futureRemoteCommitPublished = None,
        revokedCommitPublished =
          RevokedCommitPublished(
            commitTx = tx2,
            claimMainOutputTx = Some(tx3),
            mainPenaltyTx = None,
            htlcPenaltyTxs = Nil,
            claimHtlcDelayedPenaltyTxs = Nil,
            irrevocablySpent = Map.empty
          ) ::
            RevokedCommitPublished(
              commitTx = tx4,
              claimMainOutputTx = Some(tx5),
              mainPenaltyTx = None,
              htlcPenaltyTxs = Nil,
              claimHtlcDelayedPenaltyTxs = Nil,
              irrevocablySpent = Map.empty
            ) ::
            RevokedCommitPublished(
              commitTx = tx6,
              claimMainOutputTx = None,
              mainPenaltyTx = None,
              htlcPenaltyTxs = Nil,
              claimHtlcDelayedPenaltyTxs = Nil,
              irrevocablySpent = Map.empty
            ) :: Nil
      )
    ).isEmpty)

    // local close + revoked close, one revoked confirmed
    assert(Closing.isClosingTypeAlreadyKnown(
      DATA_CLOSING(
        commitments = null,
        mutualCloseProposed = Nil,
        mutualClosePublished = Nil,
        localCommitPublished = Some(LocalCommitPublished(
          commitTx = tx1,
          claimMainDelayedOutputTx = None,
          htlcSuccessTxs = Nil,
          htlcTimeoutTxs = Nil,
          claimHtlcDelayedTxs = Nil,
          irrevocablySpent = Map.empty
        )),
        remoteCommitPublished = None,
        nextRemoteCommitPublished = None,
        futureRemoteCommitPublished = None,
        revokedCommitPublished =
          RevokedCommitPublished(
            commitTx = tx2,
            claimMainOutputTx = Some(tx3),
            mainPenaltyTx = None,
            htlcPenaltyTxs = Nil,
            claimHtlcDelayedPenaltyTxs = Nil,
            irrevocablySpent = Map.empty
          ) ::
            RevokedCommitPublished(
              commitTx = tx4,
              claimMainOutputTx = Some(tx5),
              mainPenaltyTx = None,
              htlcPenaltyTxs = Nil,
              claimHtlcDelayedPenaltyTxs = Nil,
              irrevocablySpent = Map(
                tx4.txIn.head.outPoint -> tx4.txid
              )
            ) ::
            RevokedCommitPublished(
              commitTx = tx6,
              claimMainOutputTx = None,
              mainPenaltyTx = None,
              htlcPenaltyTxs = Nil,
              claimHtlcDelayedPenaltyTxs = Nil,
              irrevocablySpent = Map.empty
            ) :: Nil
      )
    ).contains(RevokedClose))

  }

}

