package fr.acinq.eclair.wire.internal.channel.version2

import fr.acinq.bitcoin.scalacompat.{ByteVector64, OutPoint, Transaction}
import fr.acinq.eclair.channel.{ChannelConfig, ChannelFeatures, HtlcTxAndRemoteSig}
import fr.acinq.eclair.wire.internal.channel.version2.ChannelCodecs2.Codecs._
import fr.acinq.eclair.wire.internal.channel.version2.ChannelCodecs2.channelDataCodec
import fr.acinq.eclair.{Features, randomBytes32}
import org.scalatest.funsuite.AnyFunSuite
import scodec.bits.HexStringSyntax

class ChannelCodecs2Spec extends AnyFunSuite {

  // Data from a channel in NORMAL state using codecs version 2.
  val dataNormal = hex""

  test("encode/decode map of spending txs") {
    val map = Map(
      OutPoint(randomBytes32(), 42) -> Transaction.read(""),
      OutPoint(randomBytes32(), 14502) -> Transaction.read(""),
      OutPoint(randomBytes32(), 0) -> Transaction.read(""),
      OutPoint(randomBytes32(), 454513) -> Transaction.read("")
    )
    assert(spentMapCodec.decodeValue(spentMapCodec.encode(map).require).require == map)
  }

  test("remove signatures from commitment txs") {
    val commitments = channelDataCodec.decode(dataNormal.bits).require.value.commitments.latest
    commitments.localCommit.commitTxAndRemoteSig.commitTx.tx.txIn.foreach(txIn => assert(txIn.witness.isNull))
    assert(commitments.localCommit.htlcTxsAndRemoteSigs.nonEmpty)
    commitments.localCommit.htlcTxsAndRemoteSigs.foreach {
      case HtlcTxAndRemoteSig(htlcTx, remoteSig) =>
        assert(remoteSig !== ByteVector64.Zeroes)
        htlcTx.tx.txIn.foreach(txIn => assert(txIn.witness.isNull))
    }
  }

  test("split channel version into channel config and channel features") {
    {
      // Standard channel
      val commitments = channelDataCodec.decode(dataNormal.bits).require.value.commitments
      assert(commitments.params.channelConfig == ChannelConfig.standard)
      assert(commitments.params.channelFeatures == ChannelFeatures())
    }
    {
      val staticRemoteKeyChannel = hex""
      val commitments = channelDataCodec.decode(staticRemoteKeyChannel.bits).require.value.commitments
      assert(commitments.params.channelConfig == ChannelConfig.standard)
      assert(commitments.params.channelFeatures == ChannelFeatures(Features.StaticRemoteKey))
    }
    {
      val anchorOutputsChannel = hex""
      val commitments = channelDataCodec.decode(anchorOutputsChannel.bits).require.value.commitments
      assert(commitments.params.channelConfig == ChannelConfig.standard)
      assert(commitments.params.channelFeatures == ChannelFeatures(Features.StaticRemoteKey, Features.AnchorOutputs))
    }
    {
      val wumboChannel = hex""
      val commitments = channelDataCodec.decode(wumboChannel.bits).require.value.commitments
      assert(commitments.params.channelConfig == ChannelConfig.standard)
      assert(commitments.params.channelFeatures == ChannelFeatures(Features.Wumbo))
    }
  }

  test("ensure remote shutdown script is not set") {
    val commitments = channelDataCodec.decode(dataNormal.bits).require.value.commitments
    assert(commitments.params.remoteParams.upfrontShutdownScript_opt.isEmpty)
  }

}
