package fr.acinq.eclair.wire.internal.channel.version2

import fr.acinq.bitcoin.{ByteVector64, OutPoint, Transaction}
import fr.acinq.eclair.channel.{ChannelConfigOptions, ChannelTypes, HtlcTxAndRemoteSig}
import fr.acinq.eclair.randomBytes32
import fr.acinq.eclair.wire.internal.channel.version2.ChannelCodecs2.Codecs._
import fr.acinq.eclair.wire.internal.channel.version2.ChannelCodecs2.stateDataCodec
import org.scalatest.funsuite.AnyFunSuite
import scodec.bits.HexStringSyntax

class ChannelCodecs2Spec extends AnyFunSuite {

  test("encode/decode map of spending txs") {
    val map = Map(
      OutPoint(randomBytes32(), 42) -> Transaction.read(""),
      OutPoint(randomBytes32(), 14502) -> Transaction.read(""),
      OutPoint(randomBytes32(), 0) -> Transaction.read(""),
      OutPoint(randomBytes32(), 454513) -> Transaction.read("")
    )
    assert(spentMapCodec.decodeValue(spentMapCodec.encode(map).require).require === map)
  }

  test("remove signatures from commitment txs") {
    val data = hex""
    val commitments = stateDataCodec.decode(data.bits).require.value.commitments
    commitments.localCommit.commitTxAndRemoteSig.commitTx.tx.txIn.foreach(txIn => assert(txIn.witness.isNull))
    assert(commitments.localCommit.htlcTxsAndRemoteSigs.nonEmpty)
    commitments.localCommit.htlcTxsAndRemoteSigs.foreach {
      case HtlcTxAndRemoteSig(htlcTx, remoteSig) =>
        assert(remoteSig !== ByteVector64.Zeroes)
        htlcTx.tx.txIn.foreach(txIn => assert(txIn.witness.isNull))
    }
  }

  test("split channel version into channel config and channel type") {
    val data = hex""
    val commitments = stateDataCodec.decode(data.bits).require.value.commitments
    assert(commitments.channelConfig === ChannelConfigOptions.standard)
    assert(commitments.channelType === ChannelTypes.standard)
  }

}
