class ChannelCodecs4Spec extends AnyFunSuite {

  test("encode/decode channel configuration options") {
    assert(channelConfigCodec.encode(ChannelConfig(Set.empty[ChannelConfigOption])).require.bytes == hex"00")
    assert(channelConfigCodec.decode(hex"00".bits).require.value == ChannelConfig(Set.empty[ChannelConfigOption]))
    assert(channelConfigCodec.decode(hex"01f0".bits).require.value == ChannelConfig(Set.empty[ChannelConfigOption]))
    assert(channelConfigCodec.decode(hex"020000".bits).require.value == ChannelConfig(Set.empty[ChannelConfigOption]))

    assert(channelConfigCodec.encode(ChannelConfig.standard).require.bytes == hex"0101")
    assert(channelConfigCodec.encode(ChannelConfig(ChannelConfig.FundingPubKeyBasedChannelKeyPath)).require.bytes == hex"0101")
    assert(channelConfigCodec.decode(hex"0101".bits).require.value == ChannelConfig(ChannelConfig.FundingPubKeyBasedChannelKeyPath))
    assert(channelConfigCodec.decode(hex"01ff".bits).require.value == ChannelConfig(ChannelConfig.FundingPubKeyBasedChannelKeyPath))
    assert(channelConfigCodec.decode(hex"020001".bits).require.value == ChannelConfig(ChannelConfig.FundingPubKeyBasedChannelKeyPath))
  }

  test("encode/decode optional channel reserve") {
    val localParams = ChannelTypes0.LocalParams(
      randomKey().publicKey,
      DeterministicWallet.KeyPath(Seq(42L)),
      Satoshi(660),
      UInt64(500_000),
      Some(Satoshi(15_000)),
      MilliSatoshi(1000),
      CltvExpiryDelta(36),
      50,
      Random.nextBoolean(),
      Random.nextBoolean(),
      Some(hex"deadbeef"),
      None,
      Features().initFeatures())
    val remoteParams = ChannelTypes4.RemoteParams(
      randomKey().publicKey,
      Satoshi(500),
      UInt64(100000),
      Some(Satoshi(30000)),
      MilliSatoshi(1500),
      CltvExpiryDelta(144),
      10,
      randomKey().publicKey,
      randomKey().publicKey,
      randomKey().publicKey,
      randomKey().publicKey,
      Features(),
      None)

    {
      val localCodec = localParamsCodec(ChannelTypes3.ChannelFeatures(Set.empty))
      val remoteCodec = remoteParamsCodec(ChannelTypes3.ChannelFeatures(Set.empty))
      val decodedLocalParams = localCodec.decode(localCodec.encode(localParams).require).require.value
      val decodedRemoteParams = remoteCodec.decode(remoteCodec.encode(remoteParams).require).require.value
      assert(decodedLocalParams == localParams)
      assert(decodedRemoteParams == remoteParams)
    }
    {
      val localCodec = localParamsCodec(ChannelTypes3.ChannelFeatures(Set(Features.DualFunding)))
      val remoteCodec = remoteParamsCodec(ChannelTypes3.ChannelFeatures(Set(Features.DualFunding)))
      val decodedLocalParams = localCodec.decode(localCodec.encode(localParams).require).require.value
      val decodedRemoteParams = remoteCodec.decode(remoteCodec.encode(remoteParams).require).require.value
      assert(decodedLocalParams == localParams.copy(initialRequestedChannelReserve_opt = None))
      assert(decodedRemoteParams == remoteParams.copy(initialRequestedChannelReserve_opt = None))
    }
  }

  test("decode unconfirmed dual funded") {
    // data encoded with the previous version of eclair, when Shared.Input did not include a pubkey script
    val raw = ByteVector.fromValidHex("")
    val decoded = fundingTxStatusCodec.decode(raw.bits).require.value.migrate(ZeroFeeHtlcTxAnchorOutputsCommitmentFormat, InputInfo(OutPoint(TxId(ByteVector32.Zeroes), 0), TxOut(1000 sat, hex"deadbeef")))

    // check that our codec will set the pubkeyscript using the one from the funding params
    val channelId = ByteVector32.Zeroes
    val script = Scripts.multiSig2of2(PrivateKey(ByteVector.fromValidHex("01" * 32)).publicKey, PrivateKey(ByteVector.fromValidHex("02" * 32)).publicKey)
    val dualFundedUnconfirmedFundingTx = LocalFundingStatus.DualFundedUnconfirmedFundingTx(
      PartiallySignedSharedTransaction(
        SharedTransaction(
          // we include the correct pubkey script here
          Some(InteractiveTxBuilder.Input.Shared(UInt64(42), OutPoint(TxId(ByteVector32.Zeroes), 0), Script.write(Script.pay2wsh(script)), 12345L, MilliSatoshi(100), MilliSatoshi(200), MilliSatoshi(300))),
          sharedOutput = InteractiveTxBuilder.Output.Shared(UInt64(43), ByteVector.fromValidHex("deadbeef"), MilliSatoshi(100), MilliSatoshi(200), MilliSatoshi(300)),
          localInputs = Nil, remoteInputs = Nil, localOutputs = Nil, remoteOutputs = Nil, lockTime = 0
        ),
        localSigs = TxSignatures(channelId, TxId(ByteVector32.Zeroes), Nil)
      ),
      createdAt = BlockHeight(1000),
      fundingParams = InteractiveTxParams(channelId = channelId, isInitiator = true, localContribution = 100.sat, remoteContribution = 200.sat,
        sharedInput_opt = Some(InteractiveTxBuilder.SharedFundingInput(
          InputInfo(OutPoint(TxId(ByteVector32.Zeroes), 0), TxOut(1000.sat, Script.pay2wsh(script))),
          0,
          PrivateKey(ByteVector.fromValidHex("02" * 32)).publicKey,
          ZeroFeeHtlcTxAnchorOutputsCommitmentFormat,
        )),
        remoteFundingPubKey = PrivateKey(ByteVector.fromValidHex("01" * 32)).publicKey,
        localOutputs = Nil,
        commitmentFormat = ZeroFeeHtlcTxAnchorOutputsCommitmentFormat,
        lockTime = 0, dustLimit = 330.sat, targetFeerate = FeeratePerByte(3.sat).perKw, requireConfirmedInputs = RequireConfirmedInputs(forLocal = false, forRemote = false)),
      liquidityPurchase_opt = None
    )
    assert(decoded == dualFundedUnconfirmedFundingTx)
  }

  test("decode dual-funded unsigned local commit") {
    val bin = ByteVector.fromValidHex("")
    val decoded = channelDataCodec.decode(bin.bits).require.value.asInstanceOf[DATA_WAIT_FOR_DUAL_FUNDING_SIGNED]
    assert(decoded.channelParams.channelFeatures.features == Set(Features.DualFunding))
    // Local params.
    assert(decoded.channelParams.localParams.isChannelOpener)
    assert(decoded.channelParams.localParams.walletStaticPaymentBasepoint.nonEmpty)
    assert(decoded.signingSession.localCommitParams.toSelfDelay == CltvExpiryDelta(144))
    assert(decoded.signingSession.localCommitParams.dustLimit == 1100.sat)
    assert(decoded.signingSession.localCommitParams.htlcMinimum == 0.msat)
    assert(decoded.signingSession.localCommitParams.maxAcceptedHtlcs == 100)
    assert(decoded.signingSession.localCommitParams.maxHtlcValueInFlight == UInt64(500_000_000))
    // Remote params.
    assert(decoded.signingSession.remoteCommitParams.toSelfDelay == CltvExpiryDelta(144))
    assert(decoded.signingSession.remoteCommitParams.dustLimit == 1000.sat)
    assert(decoded.signingSession.remoteCommitParams.htlcMinimum == 1000.msat)
    assert(decoded.signingSession.remoteCommitParams.maxAcceptedHtlcs == 30)
    assert(decoded.signingSession.remoteCommitParams.maxHtlcValueInFlight == UInt64(1_000_000_000))
    // Signing session.
    assert(decoded.signingSession.fundingTxIndex == 0)
    assert(decoded.signingSession.fundingParams.commitmentFormat == DefaultCommitmentFormat)
    assert(decoded.signingSession.fundingTx.txId == TxId.fromValidHex("43a10a9ba74d9a8cbaae953c8d7e6ac357c09698df852cf38eddc53168b5e1b0"))
    assert(decoded.signingSession.fundingTx.tx.sharedOutput.amount == 1_500_000.sat)
    assert(decoded.signingSession.fundingTx.tx.sharedOutput.pubkeyScript == hex"002059106401877b19606b711f9980431fd80e830d2a30c59103d7f12e16a7e141d3")
    assert(decoded.signingSession.fundingTx.tx.buildUnsignedTx().txOut.indexWhere(_.publicKeyScript == hex"002059106401877b19606b711f9980431fd80e830d2a30c59103d7f12e16a7e141d3") == 2)
    assert(decoded.signingSession.localCommit.isLeft)
    val unsignedLocalCommit = decoded.signingSession.localCommit.left.toOption.get
    assert(unsignedLocalCommit.index == 0)
    assert(unsignedLocalCommit.txId == TxId.fromValidHex("ab3c0f7a2242e5656cc59db4852347b9ad4c6b54186639bf3b5698df2b129f88"))
  }

  test("decode splice unsigned local commit") {
    val bin = ByteVector.fromValidHex("")
    val decoded = channelDataCodec.decode(bin.bits).require.value.asInstanceOf[DATA_NORMAL]
    assert(decoded.channelParams.channelFeatures.features == Set(Features.DualFunding))
    assert(decoded.commitments.latest.commitmentFormat == DefaultCommitmentFormat)
    assert(decoded.spliceStatus.isInstanceOf[SpliceStatus.SpliceWaitingForSigs])
    val spliceStatus = decoded.spliceStatus.asInstanceOf[SpliceStatus.SpliceWaitingForSigs]
    // Local params.
    assert(!decoded.channelParams.localParams.isChannelOpener)
    assert(decoded.channelParams.localParams.walletStaticPaymentBasepoint.nonEmpty)
    assert(spliceStatus.signingSession.localCommitParams.toSelfDelay == CltvExpiryDelta(144))
    assert(spliceStatus.signingSession.localCommitParams.dustLimit == 1000.sat)
    assert(spliceStatus.signingSession.localCommitParams.htlcMinimum == 1000.msat)
    assert(spliceStatus.signingSession.localCommitParams.maxAcceptedHtlcs == 30)
    assert(spliceStatus.signingSession.localCommitParams.maxHtlcValueInFlight == UInt64(1_000_000_000))
    // Remote params.
    assert(spliceStatus.signingSession.remoteCommitParams.toSelfDelay == CltvExpiryDelta(144))
    assert(spliceStatus.signingSession.remoteCommitParams.dustLimit == 1100.sat)
    assert(spliceStatus.signingSession.remoteCommitParams.htlcMinimum == 0.msat)
    assert(spliceStatus.signingSession.remoteCommitParams.maxAcceptedHtlcs == 100)
    assert(spliceStatus.signingSession.remoteCommitParams.maxHtlcValueInFlight == UInt64(500_000_000))
    // Signing session.
    assert(spliceStatus.signingSession.fundingTxIndex == 1)
    assert(spliceStatus.signingSession.fundingParams.commitmentFormat == DefaultCommitmentFormat)
    assert(spliceStatus.signingSession.fundingTx.txId == TxId.fromValidHex("e76ca5648d43b2cb0d53c569ef09e4cca22c644a8212dafdaef344e672c6f641"))
    assert(spliceStatus.signingSession.fundingTx.tx.sharedOutput.amount == 1_900_000.sat)
    assert(spliceStatus.signingSession.fundingTx.tx.sharedOutput.pubkeyScript == hex"002029fedca3b62676f613aaaea53ff97b497f8799f40a5dc586e67f023c5b1a5875")
    assert(spliceStatus.signingSession.fundingTx.tx.buildUnsignedTx().txOut.indexWhere(_.publicKeyScript == hex"002029fedca3b62676f613aaaea53ff97b497f8799f40a5dc586e67f023c5b1a5875") == 1)
    assert(spliceStatus.signingSession.localCommit.isLeft)
    val unsignedLocalCommit = spliceStatus.signingSession.localCommit.left.toOption.get
    assert(unsignedLocalCommit.index == 2)
    assert(unsignedLocalCommit.txId == TxId.fromValidHex("88d4950aff3bb218308d09ca650ac4648f4cbbadbd1d0f333c0e6fd0a483cf07"))
  }

  test("decode local commit with commit and htlc transactions") {
    // Before https://github.com/ACINQ/eclair/pull/3099, we stored the unsigned commit tx and HTLC txs in our local commit.
    val bin = ByteVector.fromValidHex("")
    val decoded = channelDataCodec.decode(bin.bits).require.value.asInstanceOf[DATA_NORMAL]
    assert(decoded.commitments.latest.fundingInput == OutPoint(TxId.fromValidHex("59ac1824cba876995ef868660f099caf1e6cd35d045ca99421d238f32a4fbffb"), 0))
    assert(decoded.commitments.latest.capacity == 1_000_000.sat)
    val localCommit = decoded.commitments.latest.localCommit
    assert(localCommit.index == 7)
    assert(localCommit.txId == TxId.fromValidHex("c6e967cf9f1128bf26f0f9c0e94207856022b10d97942cfb60fb2239640256dc"))
    val htlcRemoteSigs = List(
      ByteVector64(hex"65cffa80c72f9e291456fabc3771865f4993108f8a90deddf041a9bcd523d3ec2d5361430705447f87312b2b858327f5f0c4999b7bdd7da84dd270189304def1"),
      ByteVector64(hex"c5930b24a9e50aa0680daf5ab04d82e21fe1edfaf2455e07e08484660cf4c12d384c22a3aa8b32fad23ba7f451ea718c718439b0b256b120f22eeadc5b995f49"),
      ByteVector64(hex"03821c3180261d81126c2157282ea17be5131081c2d27c34f55a4000d06fc12b1a00bc7750d6e8d6765d1d53aae75cef20be8117cf7fc5c91c0ba017d3e214b1"),
    )
    assert(localCommit.htlcRemoteSigs == htlcRemoteSigs)
  }

  test("decode local params pay commit tx fees field") {
    // The data in this test was encoded using eclair v0.10.0, where a single is_initiator boolean was encoded instead
    // of two separate booleans (is_channel_opener and pay_commit_tx_fees).
    val initiator = hex""
    val initiatorDecoded = channelDataCodec.decode(initiator.bits).require.value.asInstanceOf[DATA_NORMAL]
    assert(initiatorDecoded.commitments.localChannelParams.isChannelOpener)
    assert(initiatorDecoded.commitments.localChannelParams.paysCommitTxFees)
    val nonInitiator = hex""
    val nonInitiatorDecoded = channelDataCodec.decode(nonInitiator.bits).require.value.asInstanceOf[DATA_NORMAL]
    assert(!nonInitiatorDecoded.commitments.localChannelParams.isChannelOpener)
    assert(!nonInitiatorDecoded.commitments.localChannelParams.paysCommitTxFees)
  }

  test("decode splice funding status") {
    val bin = hex""
    val decoded = channelDataCodec.decode(bin.bits).require.value.asInstanceOf[DATA_NORMAL]
    assert(decoded.spliceStatus == SpliceStatus.NoSplice)
    assert(decoded.closeStatus_opt.isEmpty)
    assert(decoded.commitments.active.size == 3)
    assert(decoded.commitments.all.size == 3)
    decoded.commitments.active.foreach(c => assert(c.commitmentFormat == ZeroFeeHtlcTxAnchorOutputsCommitmentFormat))
    assert(decoded.commitments.channelParams.channelFeatures.features == Set(Features.DualFunding))
    // Local params.
    assert(!decoded.commitments.localChannelParams.isChannelOpener)
    assert(decoded.commitments.localChannelParams.walletStaticPaymentBasepoint.isEmpty)
    assert(decoded.commitments.latest.localCommitParams.toSelfDelay == CltvExpiryDelta(144))
    assert(decoded.commitments.latest.localCommitParams.dustLimit == 1000.sat)
    assert(decoded.commitments.latest.localCommitParams.htlcMinimum == 1000.msat)
    assert(decoded.commitments.latest.localCommitParams.maxAcceptedHtlcs == 30)
    assert(decoded.commitments.latest.localCommitParams.maxHtlcValueInFlight == UInt64(1_000_000_000))
    // Remote params.
    assert(decoded.commitments.latest.remoteCommitParams.toSelfDelay == CltvExpiryDelta(144))
    assert(decoded.commitments.latest.remoteCommitParams.dustLimit == 1100.sat)
    assert(decoded.commitments.latest.remoteCommitParams.htlcMinimum == 0.msat)
    assert(decoded.commitments.latest.remoteCommitParams.maxAcceptedHtlcs == 100)
    assert(decoded.commitments.latest.remoteCommitParams.maxHtlcValueInFlight == UInt64(500_000_000))
    // The latest splice is unconfirmed.
    assert(decoded.commitments.active.head.fundingTxIndex == 2)
    assert(decoded.commitments.active.head.firstRemoteCommitIndex == 1)
    assert(decoded.commitments.active.head.remoteFundingPubKey == PublicKey(hex"02ab7a85d532b3cf955d440c7b92a368c65067cd05588385ae3bbb45d8187825da"))
    assert(decoded.commitments.active.head.fundingTxId == TxId.fromValidHex("56deed630c69b67f1982e1ea3d872081d780f830741823bcd5d19e43c266d115"))
    assert(decoded.commitments.active.head.fundingInput == OutPoint(TxId.fromValidHex("56deed630c69b67f1982e1ea3d872081d780f830741823bcd5d19e43c266d115"), 0))
    assert(decoded.commitments.active.head.fundingAmount == 2_400_000.sat)
    assert(decoded.commitments.active.head.commitTxIds == CommitTxIds(TxId.fromValidHex("99db32761e7adfe0311fc55cd09e6b885b9da5306aec45f18a094fec153747a5"), TxId.fromValidHex("a46aa4f35b1c3ebd340deb44bf796b673f59c76d526a4559bd3de93368538715"), None))
    assert(decoded.commitments.active.head.localFundingStatus.isInstanceOf[LocalFundingStatus.DualFundedUnconfirmedFundingTx])
    assert(decoded.commitments.active.head.localFundingStatus.signedTx_opt.contains(Transaction.read("")))
    assert(decoded.commitments.active.head.remoteFundingStatus == RemoteFundingStatus.NotLocked)
    // The previous splice is confirmed but not locked by the remote node.
    assert(decoded.commitments.active(1).fundingTxIndex == 1)
    assert(decoded.commitments.active(1).firstRemoteCommitIndex == 1)
    assert(decoded.commitments.active(1).remoteFundingPubKey == PublicKey(hex"02db80bf3ce539911d400ad7c8fc6455f2919171601cdb6dd1eaba9a13ec6fd29d"))
    assert(decoded.commitments.active(1).fundingTxId == TxId.fromValidHex("344dfc7fb34cc2c27937fa0cbeb5b15b9cb0dcc692e1177b3dcdc30f0f5c6ea9"))
    assert(decoded.commitments.active(1).fundingInput == OutPoint(TxId.fromValidHex("344dfc7fb34cc2c27937fa0cbeb5b15b9cb0dcc692e1177b3dcdc30f0f5c6ea9"), 1))
    assert(decoded.commitments.active(1).fundingAmount == 1_900_000.sat)
    assert(decoded.commitments.active(1).commitTxIds == CommitTxIds(TxId.fromValidHex("7ba7b4e5bc38a5539fc4af56886665324dae2388a36062dbc02a3b8ad62abd2b"), TxId.fromValidHex("0edc489971adba4e677a7d5fef263e13620dcd03963aadb7c52bc164a66307ab"), None))
    assert(decoded.commitments.active(1).localFundingStatus.isInstanceOf[LocalFundingStatus.ConfirmedFundingTx])
    assert(decoded.commitments.active(1).localFundingStatus.asInstanceOf[LocalFundingStatus.ConfirmedFundingTx].txOut == Transaction.read("").txOut(1))
    assert(decoded.commitments.active(1).remoteFundingStatus == RemoteFundingStatus.NotLocked)
    // The initial funding transaction is confirmed and locked.
    assert(decoded.commitments.active.last.fundingTxIndex == 0)
    assert(decoded.commitments.active.last.firstRemoteCommitIndex == 0)
    assert(decoded.commitments.active.last.remoteFundingPubKey == PublicKey(hex"039f967afce64ab9a5da4edbac9f3f0dddb2bdec02d12c124c415690e588eb50a0"))
    assert(decoded.commitments.active.last.fundingTxId == TxId.fromValidHex("1f4a51c13c5d2572a3a9fdd6aa8236da587448cf4a077e3c33c2783b9db3b84c"))
    assert(decoded.commitments.active.last.fundingInput == OutPoint(TxId.fromValidHex("1f4a51c13c5d2572a3a9fdd6aa8236da587448cf4a077e3c33c2783b9db3b84c"), 0))
    assert(decoded.commitments.active.last.fundingAmount == 1_500_000.sat)
    assert(decoded.commitments.active.last.commitTxIds == CommitTxIds(TxId.fromValidHex("7637465daef2054492a18c48e33e4b67a5abb832fb4f36bc0de8006ed23ab72e"), TxId.fromValidHex("7628ac5ffaec424592e2982d06f0d33af3a11a433a49ae2a34d0ffbca4354af5"), None))
    assert(decoded.commitments.active.last.localFundingStatus.isInstanceOf[LocalFundingStatus.ConfirmedFundingTx])
    assert(decoded.commitments.active.last.localFundingStatus.asInstanceOf[LocalFundingStatus.ConfirmedFundingTx].txOut == Transaction.read("").txOut(0))
    assert(decoded.commitments.active.last.remoteFundingStatus == RemoteFundingStatus.Locked)
  }

  test("encode/decode cold origins") {
    val origins = Map(
      13L -> Origin.Cold(Upstream.Cold.Channel(randomBytes32(), 2765, 1863 msat)),
      27L -> Origin.Cold(Upstream.Cold.Trampoline(Upstream.Cold.Channel(randomBytes32(), 541, 6500 msat) :: Nil)),
      28L -> Origin.Cold(Upstream.Cold.Trampoline(Upstream.Cold.Channel(randomBytes32(), 0, 0 msat) :: Upstream.Cold.Channel(randomBytes32(), 6778, 250_000_001 msat) :: Nil)),
    )
    val encoded = originsMapCodec.encode(origins).require
    val decoded = originsMapCodec.decode(encoded).require.value
    assert(decoded == origins)
  }

  test("decode origin backwards compatibility") {
    // The following values were encoded with eclair v0.10.0.
    val channelRelayed = Origin.Cold(Upstream.Cold.Channel(ByteVector32(hex"fe99ac49738d44ff8b73d8e5da01e868584a2071326320e8f51fe8bdbbe84c64"), 1561, 1721 msat))
    val channelRelayedBin = hex"0002fe99ac49738d44ff8b73d8e5da01e868584a2071326320e8f51fe8bdbbe84c64000000000000061900000000000006b900000000000005dc"
    assert(originCodec.decode(channelRelayedBin.bits).require.value == channelRelayed)
    val trampolineRelayed = Origin.Cold(Upstream.Cold.Trampoline(Upstream.Cold.Channel(ByteVector32(hex"19a3e2f5b1d747e6e59cec57d927c068c976eab0b914a1bf66aaacaa0917d49d"), 17, 0 msat) :: Upstream.Cold.Channel(ByteVector32(hex"4fbf1090bf27e4ef3af8b784f8e0e62dd2fc836775131d6e58400a68ec8fcf2c"), 21, 0 msat) :: Nil))
    val trampolineRelayedBin = hex"0004000219a3e2f5b1d747e6e59cec57d927c068c976eab0b914a1bf66aaacaa0917d49d00000000000000114fbf1090bf27e4ef3af8b784f8e0e62dd2fc836775131d6e58400a68ec8fcf2c0000000000000015"
    assert(originCodec.decode(trampolineRelayedBin.bits).require.value == trampolineRelayed)
  }

  test("include htlc_id for closing channel") {
    val closing1 = channelDataCodec.decode(hex"".bits).require.value.asInstanceOf[DATA_CLOSING]
    assert(closing1.localCommitPublished.nonEmpty)
    val lcp = closing1.localCommitPublished.get
    assert(lcp.localOutput_opt.nonEmpty)
    assert(lcp.incomingHtlcs.values.toSeq == Seq(0))
    assert(lcp.outgoingHtlcs.values.toSeq == Seq(3))
    assert(lcp.htlcDelayedOutputs.size == 2)

    val closing2 = channelDataCodec.decode(hex"".bits).require.value.asInstanceOf[DATA_CLOSING]
    assert(closing2.remoteCommitPublished.nonEmpty)
    val rcp = closing2.remoteCommitPublished.get
    assert(rcp.outgoingHtlcs.isEmpty)
    assert(rcp.incomingHtlcs.values.toSeq == Seq(0))

    val closing3 = channelDataCodec.decode(hex"".bits).require.value.asInstanceOf[DATA_CLOSING]
    assert(closing3.nextRemoteCommitPublished.nonEmpty)
    val nrcp = closing3.nextRemoteCommitPublished.get
    assert(nrcp.incomingHtlcs.values.toSeq == Seq(0))
    assert(nrcp.outgoingHtlcs.values.toSeq == Seq(3))
  }

}
