  test("decode/encode cln message") {
    val bin = hex""
    val peerswap_decoded = peerSwapMessageCodecWithFallback.decode(bin.bits).require
    assert(peerswap_decoded.value.isInstanceOf[SwapInRequest])

    val lightning_decoded = lightningMessageCodecWithFallback.decode(bin.bits).require
    val unknownMessage = lightning_decoded.value.asInstanceOf[UnknownMessage]
    peerSwapMessageCodecWithFallback.decode(unknownMessageCodec.encode(unknownMessage).require)

    val lightning_encoded = lightningMessageCodecWithFallback.encode(unknownMessage).require
    assert(lightning_encoded.toByteVector == bin)
  }

